import { CategoriesDropdownMenu as Users } from "../User/DropdownUser";
import { Icon as CartIcon } from "../Cart/icon";
import { Icon as WishlistIcon } from "../Wishlist/icon";
import { Icon as CompareIcon } from "../Compare/icon";
import { Icon as OrderIcon } from "../Order/icon";
import { CategoriesDropdownMenu } from "../Category/DropdownMenu";
import { Link } from "../src/Link";
import {
  Collapse,
  div,
  divToggler,
  divBrand,
  Nav,
  NavItem,
  divText,
  Container,
  NavLink,
  Form,
  ButtonToggle,
} from "reactstrap";
import { useState, useEffect, Fragment } from "react";
import { Search } from "../Search";
import { page } from "../../config/yensaodatquang.json";
import { Logo } from "../src/logo";
import { IoIosMenu, IoIosArrowBack } from "react-icons/io";
import { FiFilter } from "react-icons/fi";
import { useRouter } from "next/router";
import theme from "../src/theme";
import { useSpring, animated } from "react-spring";
export default function MenuApp({ isOpen, toggle }) {
  const [isFixed, setIsFixed] = useState(false);
  const [innerWidth, setInnerWidth] = useState();
  const router = useRouter();
  const [onScroll, setOnScroll] = useState(false);
  useEffect(() => {
    setInnerWidth(window.innerWidth);
    window.onscroll = function () {
      const scroll = document.scrollingElement.scrollTop;
      if (scroll === 0) {
        if (onScroll == true) setOnScroll(false);
      } else if (onScroll === false) setOnScroll(true);
    };
  });
  const css = {
    icon: {
      display: innerWidth < 768 ? "inline-block" : "none",
      fontSize: "1rem",
      padding: theme.spacing(2),
    },
  };

  // ANIMATION
  const inputSpring = useSpring({
    opacity: onScroll ? 1 : 0,
    border: onScroll ? "" : "none",
    display: onScroll ? "inline-block" : "none",
    width: "100%",
    from: { opacity: onScroll ? 0 : 1 },
  });
  const h1Sr = useSpring({});
  return (
    <div
      style={{
        position: "fixed",
        backgroundColor: theme.backgroundColor,
        width: "100%",
        zIndex: 100,
        boxShadow: `3px 3px 15px ${theme.bgDark}`,
        padding: theme.spacing(1),
      }}
    >
      <Container style={{ padding: 0 }}>
        {/* DISPLAY IN MOBILE */}
        {innerWidth < 768 ? (
          <div
            style={{
              display: "flex",
              justifyItems: "center",
              justifyContent: "center",
              alignItems: "center",
              alignContent: "center",
            }}
          >
            <Link
              style={{
                ...css.icon,
                marginRight: theme.spacing(1),
                float: "left",
              }}
              onClick={() => {
                router.back();
              }}
            >
              <IoIosArrowBack />
            </Link>
            {/* LOGO */}
            <div
              style={{ marginLeft: "auto", marginRight: "auto", width: "100%" }}
            >
              <animated.input style={inputSpring} />
              <animated.h1
                style={{
                  ...h1Spring,
                  height: "100%",
                  fontSize: "1rem",
                  padding: 0,
                  margin: 0,
                  display: onScroll ? "none" : "inline-block",
                }}
                onClick={() => {
                  router.push("/");
                }}
              >
                {page.name}
              </animated.h1>
            </div>
            {/* <Search
            style={{
              padding: theme.spacing(1),
              marginLeft: theme.spacing(3),
              display: "inline-block",
            }}
          /> */}
            {/* CART ICON */}
            <CartIcon style={{ ...css.icon, float: "right" }} />
            {/* ICON MENU */}
            <Link
              style={{
                ...css.icon,
                float: "right",
                marginLeft: theme.spacing(1),
                paddingLeft: theme.spacing(3),
                paddingRight: theme.spacing(3),
              }}
              onClick={toggle}
            >
              <IoIosMenu />
            </Link>
          </div>
        ) : null}
        <Collapse isOpen={isOpen} div>
          <Nav div style={{ width: "80%", marginRight: "auto" }}>
            <Search />
          </Nav>
          <Nav
            className="hide-sm"
            style={{
              display: "flex",
              alignItems: "center",
              justifyContent: "center",
            }}
          >
            {/* Compare */}

            <CompareIcon page style={{ marginRight: theme.spacing(5) }} />
            {/* Wishlist */}

            <WishlistIcon page />

            {/* Cart */}
            <Link
              style={{
                ...css.icon,
                display: innerWidth < 768 ? "none" : "inline-block",
              }}
            >
              <CartIcon page />
            </Link>
            {/* Order */}

            <OrderIcon page />

            <Users />
          </Nav>
        </Collapse>
      </Container>
    </div>
  );
}
