import { theme } from "../../config";

export const TextWrap = ({ children }) => {
  return (
    <div style={{ display: "flex", justifyContent: "center" }}>
      <div style={{ width: "100%", maxWidth: 600, padding: theme.display(4) }}>
        {children}
      </div>
    </div>
  );
};
