import { Fragment } from "react";
import { gql, useQuery } from "@apollo/client";
import { Item as Category } from "../Category/item";
import { Item as Attributes } from "../Attribute/item";
const GET_CATE = gql`
  query($category: String, $attributes: [String]) {
    allCategories(where: { url: $category }) {
      id
      name
      url
    }
    allAttributes(where: { url_in: $attributes }) {
      id
      name
      url
    }
  }
`;
export const Filter = ({
  category = "-",
  brand,
  search,
  price_from,
  price_to,
  attributes,
}) => {
  const variables = {
    category,
    attributes: attributes ? attributes?.map((attr) => attr.url) : [],
  };
  console.log(attributes);

  const { data, error } = useQuery(GET_CATE, {
    variables,
  });
  console.log(data, error);

  return (
    <Fragment>
      <Category categories={data?.allCategories || []} />
    </Fragment>
  );
};
