import { Row, Container, Col } from "reactstrap";
import { theme } from "../../config";
import { css } from "../src/css";

const Item = ({ src, title = "", content = "" }) => {
  return (
    <div
      style={{
        position: "relative",
      }}
    >
      <img
        src={src}
        style={{ width: "100%", borderRadius: theme.spacing(2) }}
      />
      <div
        style={{
          position: "absolute",
          borderRadius: theme.spacing(2),
          zIndex: 2,
          top: 0,
          width: "100%",
          height: "100%",
          backgroundColor: "rgba(0,0,0,0.3)",
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
          color: "white",
        }}
      >
        <div>
          <h2 style={{ fontWeight: "bold" }}>{title}</h2>
          <p style={{ textTransform: "uppercase", fontWeight: "bold" }}>
            {content}
          </p>
        </div>
      </div>
    </div>
  );
};
export const Feature = () => {
  return (
    <section
      style={{
        marginTop: theme.spacing(7),
        marginBottom: theme.spacing(7),
        paddingTop: theme.spacing(6),
        paddingBottom: theme.spacing(6),
        backgroundColor: theme.backgroundColor,
      }}
    >
      <Container>
        <h5
          style={{
            textTransform: "uppercase",
            textAlign: "center",
            color: theme.secondary,
            fontSize: "1rem",
            marginBottom: theme.spacing(3),
          }}
        >
          Thương Hiệu
        </h5>
        <h2 style={{ ...css.h2, textAlign: "center", fontWeight: "bold" }}>
          Chất Lượng. Cao Cấp. Tinh Túy.
        </h2>
        <p style={{ textAlign: "center", marginBottom: theme.spacing(5) }}>
          Yến Sào Đất Quảng tự vào với chất lượng cao cấp, sự tinh túy được quê
          hương Quảng Nam ban tặng.
        </p>
        <Row style={{ marginBottom: theme.spacing(5) }}>
          <Col xs={12} md={4}>
            <Item
              src={"/yensaodatquang/assets/img/xay-dung-nha-yen.JPG"}
              title="Xây Dựng"
              content="Hang đá nhân tạo"
            />
          </Col>
          <Col xs={12} md={4}>
            <Item
              src={"/yensaodatquang/assets/img/khai-thac-to-yen.JPG"}
              title="Khai Thác"
              content="Tuyển chọn tổ tốt nhất"
            />
          </Col>
          <Col xs={12} md={4}>
            <Item
              src={"/yensaodatquang/assets/img/gia-cong-to-yen.JPG"}
              title="Gia Công"
              content="Làm sạch lông thủ công"
            />
          </Col>
        </Row>
        <div style={{ display: "flex", justifyContent: "center" }}>
          <p style={{ color: theme.secondary, maxWidth: 400 }}>
            Yến Sào được xếp vào bát trân (8 món ăn thuộc hàng tuyệt phẩm, chỉ
            vua chúa mới được sử dụng), bên cạnh nem công, chả phượng, da tê
            ngưu, bàn tay gấu, gân nai, môi đười ươi và thịt chân voi. Tất cả
            được xem là tinh túy của thế giới muốn loài, được vua Minh Mạng ví
            như "mảnh trăng non thuần khiết".
          </p>
        </div>
      </Container>
    </section>
  );
};
