import { gql, useQuery, useMutation } from "@apollo/client";

import { emptyCart } from "../../apollo/action";
import {
  customerVar,
  newOrderVar,
  newOrderCountVar,
} from "../../apollo/client";
import { Item as CartItem } from "./item";
import { List } from "./list";
import { useRouter } from "next/router";
import { page } from "../../config/index";

import { formatMoney, getErrorMessage } from "../../lib/chip";
import { Fragment } from "react";
import { css } from "../src/css";
import theme from "../src/theme";
import { Loading } from "../src/Loading";
import { Link } from "../src/Link";
import { List as Customer } from "../Customer/CurrentCustomer";
export const CART = gql`
  query {
    cartItems @client
    user @client
  }
`;
// create orderItem
export const CREATE_ORDER_ITEMS = gql`
  mutation($data: [OrderItemsCreateInput]) {
    createOrderItems(data: $data) {
      id
    }
  }
`;

// create Order
export const CREATE_ORDER = gql`
  mutation(
    $items: [OrderItemWhereUniqueInput]
    $customer: CustomerWhereUniqueInput
    $ofSeller: UserWhereUniqueInput
    $total: Int
  ) {
    createOrder(
      data: {
        customer: { connect: $customer }
        items: { connect: $items }
        ofSeller: { connect: $ofSeller }
        total: $total
      }
    ) {
      id
      seller {
        id
        name
      }
      items {
        id
        product {
          image {
            publicUrl
          }
          images {
            file {
              publicUrl
            }
          }
          name
        }
        price
        quantity
        attributes {
          id
          name
        }
      }
    }
  }
`;
const OrderItemsCreateInput = (item) => ({
  data: {
    product: { connect: { id: item.product.id } },
    price: item.price,
    quantity: item.quantity,
  },
});
const OrderItemsCreateInputs = (cartItems) =>
  cartItems.map((item) => OrderItemsCreateInput(item));

export function Cart() {
  const router = useRouter();

  // loading data
  const { data: cartData, loading, error } = useQuery(CART);
  if (loading || error || !cartData) return null;

  const { customer, cartItems, user: isLogin } = cartData;
  let sum = 0;
  cartItems?.map((cartItem) => {
    sum += cartItem.price;
  });

  // mutation
  const [createOrderItems] = useMutation(CREATE_ORDER_ITEMS);
  const [createOrder] = useMutation(CREATE_ORDER);
  // create OrderItemsCreateInputs (GraphQL type)
  let dataOICI = OrderItemsCreateInputs(cartItems);
  dataOICI.map(
    (data) => (data.data.ofSeller = { connect: { id: page.seller.id } })
  );

  // function
  const order = async (e) => {
    e.preventDefault();
    e.stopPropagation();

    if (dataOICI?.length && isLogin) {
      // create cart items
      const { data: dataCreateOrderItems, error } = await createOrderItems({
        variables: {
          data: dataOICI,
        },
      });
      // creat cart and connect cart items
      if (dataCreateOrderItems.createOrderItems) {
        let variables = {
          items: dataCreateOrderItems.createOrderItems.map((e) => ({
            id: e.id,
          })),
          customer: { id: customer.id },
          ofSeller: { id: page.seller.id },
          total: sum,
        };
        try {
          const { data: dataCreateOrder, errors: ER } = await createOrder({
            variables,
          });
          newOrderVar(true);
          newOrderCountVar(true);
          emptyCart();
          router.push({ pathname: "/order" });
        } catch {}
      }
    }
  };

  return isLogin ? (
    <div>
      <Customer />
      <List cartItems={cartItems} />
      {customer}
    </div>
  ) : (
    <div style={{ marginBottom: theme.spacing(4) }}>
      <Link href={{ pathname: "/signin", query: { redirect: "/cart" } }}>
        Bấm vào đây để đăng nhập và tiếp tục
      </Link>
    </div>
  );
}
