import { gql, useQuery } from "@apollo/client";

import { Item } from "./item";

import { page, theme } from "../../config/index";

import { Loading } from "../src/Loading";
import { useRouter } from "next/router";
import { css } from "../src/css";
import { useState } from "react";
import { IoIosList } from "react-icons/io";

const GET_CATEGORIES = gql`
  query($seller: UserWhereInput, $category: String, $root: Boolean) {
    allCategories(
      where: {
        seller: $seller
        OR: [{ url: $category }, { parent_is_null: $root }]
      }
    ) {
      id
      name
      label
      url
      parent {
        url
      }
      childs {
        id
        name
        label
        url
      }
    }
  }
`;
export function List() {
  const router = useRouter();
  const { category } = router.query;
  const { data, loading, error } = useQuery(GET_CATEGORIES, {
    variables: {
      seller: page.seller,
      category,
      root: category ? false : true,
    },
  });
  const [open, setOpen] = useState(false);
  if (error) return <i>{error}</i>;
  if (loading) return <Loading />;
  return !loading && data ? (
    <div>
      <h5 style={css.h5}>
        <IoIosList style={css.iconHeader} />
        {category ? data.allCategories[0].name : "Danh Mục"}
      </h5>

      {category
        ? data.allCategories[0].childs.map((cate) => (
            <Item
              key={cate.id}
              category={cate}
              style={{ display: "block", marginBottom: theme.spacing(2) }}
            />
          ))
        : data.allCategories
            ?.slice(0, open ? data.allCategories.length : 7)
            .map((category) => (
              <Item
                key={category.id}
                category={category}
                style={{ display: "block", marginBottom: theme.spacing(2) }}
              />
            ))}
      {data?.allCategories?.length > 7 ? (
        open ? (
          <a onClick={() => setOpen(false)} style={{ color: theme.primary }}>
            Rút gọn
          </a>
        ) : (
          <a onClick={() => setOpen(true)} style={{ color: theme.primary }}>
            Xem thêm
          </a>
        )
      ) : null}

      <Item
        category={{
          id: "all-category",
          name: category ? "Trở lại" : null,
          url: category.url,
        }}
        style={{
          display: "block",
          marginBottom: theme.spacing(2),
          color: theme.primary,
        }}
      />
    </div>
  ) : null;
}
