import theme from "../src/theme";
export const css = {
  input: {
    margin: theme.spacing(0),
    width: "100%",
    border: "none",
    borderColor: theme.secondary,
    borderBottom: "1px solid",
    "::placeholder": { color: theme.color },
  },
};
