import { CategoriesDropdownMenu as Users } from "../User/DropdownUser";
import { Icon as CartIcon } from "../Cart/icon";
import { Icon as WishlistIcon } from "../Wishlist/icon";
import { Icon as CompareIcon } from "../Compare/icon";
import { Icon as OrderIcon } from "../Order/icon";
import { CategoriesDropdownMenu } from "../Category/DropdownMenu";
import { Link } from "../src/Link";
import {
  Collapse,
  div,
  divToggler,
  divBrand,
  Nav,
  NavItem,
  divText,
  Container,
  NavLink,
  Form,
  ButtonToggle,
} from "reactstrap";
import { useState, useEffect, Fragment } from "react";
import { Search } from "../Search";
import { page } from "../../config/yensaodatquang.json";
import { Logo } from "../src/logo";
import { IoIosMenu, IoIosArrowBack } from "react-icons/io";
import { FiFilter } from "react-icons/fi";
import { useRouter } from "next/router";
import theme from "../src/theme";
import { useSpring, animated } from "react-spring";
import { compact } from "@apollo/client/utilities";
export default function MenuApp({ isOpen, toggle, onScroll, innerWidth }) {
  const router = useRouter();

  const css = {
    icon: {
      display: innerWidth < 768 ? "inline-block" : "none",
      fontSize: "1rem",
      padding: theme.spacing(2),
    },
  };

  // ANIMATION
  const inputSpring = useSpring({
    opacity: onScroll ? 1 : 0,
    border: onScroll ? "" : "none",
    display: onScroll ? "inline-block" : "none",
    from: {
      opacity: !onScroll ? 1 : 0,
      border: !onScroll ? "" : "none",
      display: !onScroll ? "inline-block" : "none",
    },
  });
  const h1Spring = useSpring({
    display: onScroll ? "none" : "inline-block",
    opacity: onScroll ? 0 : 1,
    from: {
      display: !onScroll ? "none" : "inline-block",
      opacity: !onScroll ? 0 : 1,
    },
  });
  return (
    <div
      style={{
        position: "fixed",
        backgroundColor: theme.backgroundColor,
        width: "100%",
        zIndex: 100,
        boxShadow: `3px 3px 15px ${theme.bgDark}`,
        padding: theme.spacing(1),
      }}
    >
      <Container style={{ padding: 0 }}>
        {/* DISPLAY IN MOBILE */}
        {innerWidth < 768 ? (
          <div
            style={{
              display: "flex",
              justifyItems: "center",
              justifyContent: "center",
              alignItems: "center",
              alignContent: "center",
            }}
          >
            <Link
              style={{
                ...css.icon,
                marginRight: theme.spacing(1),
                float: "left",
              }}
              onClick={() => {
                router.back();
              }}
            >
              <IoIosArrowBack />
            </Link>
            {/* LOGO */}
            <div
              style={{ marginLeft: "auto", marginRight: "auto", width: "100%" }}
            >
              <animated.input
                style={{
                  ...inputSpring,
                  width: "100%",
                  height: 30,
                  border: `1px solid ${theme.secondary}`,
                  borderRadius: theme.spacing(1),
                }}
              />
              <animated.h1
                style={{
                  ...h1Spring,
                  margin: 0,
                  padding: 0,
                  fontSize: "1.2rem",
                  color: theme.primary,
                  fontWeight: "bold",
                }}
                onClick={() => {
                  router.push("/");
                }}
              >
                {page.name}
              </animated.h1>
            </div>
            {/* <Search
            style={{
              padding: theme.spacing(1),
              marginLeft: theme.spacing(3),
              display: "inline-block",
            }}
          /> */}
            {/* CART ICON */}
            <CartIcon style={{ ...css.icon, float: "right" }} />
            {/* ICON MENU */}
            <Link
              style={{
                ...css.icon,
                float: "right",
                marginLeft: theme.spacing(1),
                paddingLeft: theme.spacing(3),
                paddingRight: theme.spacing(3),
              }}
              onClick={toggle}
            >
              <IoIosMenu />
            </Link>
          </div>
        ) : null}
        <Collapse isOpen={isOpen} div>
          <Nav div style={{ width: "80%", marginRight: "auto" }}>
            <Search />
          </Nav>
          <Nav
            className="hide-sm"
            style={{
              display: "flex",
              alignItems: "center",
              justifyContent: "center",
            }}
          >
            {/* Compare */}

            <CompareIcon page style={{ marginRight: theme.spacing(5) }} />
            {/* Wishlist */}

            <WishlistIcon page />

            {/* Cart */}
            <Link
              style={{
                ...css.icon,
                display: innerWidth < 768 ? "none" : "inline-block",
              }}
            >
              <CartIcon page />
            </Link>
            {/* Order */}

            <OrderIcon page />

            <Users />
          </Nav>
        </Collapse>
      </Container>
    </div>
  );
}
