import { filterCategoryVar, filterAttributeVar } from "../../apollo/client";
import { Fragment } from "react";
import { gql, useQuery } from "@apollo/client";
const GET_CATE = gql`
  query($category: String) {
    allCategories(where: { url: $category }) {
      id
      name
    }
  }
`;
export const Filter = ({
  category,
  brand,
  search,
  price_from,
  price_to,
  attributes,
}) => {
  console.log(category);
  const { data } = useQuery(GET_CATE, { variables: { category } });

  return <Fragment>{data?.allCategories[0]?.name}</Fragment>;
};
