import { useState, useEffect, Fragment, useContext } from "react";
import { gql, useQuery, rewriteURIForGET } from "@apollo/client";
import { Item as Post } from "./item";
import { Container, Row, Col, Spinner } from "reactstrap";

import { toSlug } from "../../lib/chip";
import { Loading } from "../src/Loading";

import { SellerContext } from "../src/SellerProvider";
const GET_POSTS = gql`
  query(
    $first: Int
    $skip: Int
    $hashtag: HashtagWhereInput
    $seller: UserWhereInput
    $except: String
    $keyword: String
  ) {
    allPosts(
      first: $first
      skip: $skip
      where: {
        AND: [
          { url_contains: $keyword }
          { hashtags_every: $hashtag }
          { seller: $seller }
          { url_not: $except }
        ]
      }
    ) {
      id
      url
    }
  }
`;
export const List = ({
  title = "",
  first = 4,
  skip = 0,
  xs,
  sm,
  md,
  lg,
  xl,
  more = true,
  except,
  center,
  hashtag,
  keyword,
}) => {
  const theme = useContext(SellerContext);
  let variables = {
    first,
    skip,

    hashtag: hashtag ? { url_contains: hashtag } : null,
    keyword,
    seller: theme.seller,

    except,
  };

  let { data, error, loading, fetchMore } = useQuery(GET_POSTS, {
    variables,
  });
  let [on, setOn] = useState(true);u
  let width = 0;
  useEffect(() => {
    // fetch more data
    if (more) {
      const postsEle = document.getElementById("posts");
      // hight of element - scrollTop < window height ?
      const alpha = () =>
        postsEle?.clientHeight -
        document.scrollingElement.scrollTop -
        window.innerHeight +
        150;

      if (alpha() > 0) setOn(false); // loading done
      if (on && !loading && alpha() < 0) {
        loadingMore();
      }

      window.onscroll = () => {
        if (alpha() < 0 && !on) setOn(true);
      };
    }
    if (!width) width = window.innerWidth;
  });
  function loadingMore() {
    const count = data?.allPosts?.length;
    variables.skip = count;
    try {
      fetchMore({
        variables,
        updateQuery: (prev, { fetchMoreResult }) => {
          if (!fetchMoreResult.allPosts?.length) {
            return prev;
          }
          return Object.assign({}, prev, {
            allPosts: [...prev.allPosts, ...fetchMoreResult.allPosts],
          });
        },
      });
    } catch (e) {}
  }
  if (error || loading)
    return (
      <div style={{ margin: theme.spacing(3) }}>
        <Loading />
      </div>
    );
  return data?.allPosts?.length ? (
    <section
      id="posts"
      style={{
        paddingLeft: theme.spacing(2),
      }}
    >
      {title ? (
        <h2
          style={{
            ...theme.css.h2,
            textAlign: "center",
            marginBottom: theme.spacing(5),
          }}
        >
          {title}
        </h2>
      ) : null}
      <Row noGutters style={{ display: "flex", justifyContent: "center" }}>
        {data.allPosts.map((post) => (
          <Col
            key={post.id}
            xs={data?.allPosts?.length > 2 ? (xs ? xs : 6) : 12}
            sm={data?.allPosts?.length > 2 ? (sm ? sm : 6) : 6}
            md={data?.allPosts?.length > 2 ? (md ? md : 4) : 4}
            lg={data?.allPosts?.length > 2 ? (lg ? lg : 3) : 3}
            xl={data?.allPosts?.length > 2 ? (xl ? xl : 2) : 3}
            style={{
              padding: width <= 768 && width != 0 ? 3 : theme.spacing(2),
              paddingTop: 0,
            }}
          >
            <Post post={post} />
          </Col>
        ))}
      </Row>
    </section>
  ) : (
    <p style={{ textAlign: "center" }}>Không có kết quả!</p>
  );
};
