import { Fragment } from "react";
const GET_CUSTOMERS = gql`
  query {
    allCustomers {
      id
      name
      phone
      address
    }
  }
`;
export const ChooseCustomer = () => {
  const { data, loading, error, refetch } = useQuery(GET_CUSTOMERS);
  if (data?.allCustomers?.length) {
    chooseCustomer({ customer: data?.allCustomers[0] });
  }
  if (loading) return <Loading />;
  if (error) return null;
  return (
    <Fragment>
      <h5 style={{ ...css.h5, display: "inline-block" }}>
        Thông Tin Người Nhận
      </h5>
      <a
        style={{
          float: "right",
          display: "inline-block",
          color: theme.secondary,
        }}
      >
        Thay đổi
      </a>
      <div style={{ marginBottom: theme.spacing(3) }}>
        {customer?.id ? (
          <Fragment>
            <a style={css.a}>{customer.name}</a>
            <a style={css.a}>{customer.phone}</a>
            <a style={css.a}>{customer.address}</a>
          </Fragment>
        ) : (
          "Bạn chưa chọn địa chỉ nhận."
        )}
      </div>
      u
    </Fragment>
  );
};
