import { IoIosPlayCircle, IoIosPlay } from "react-icons/io";
import { useState } from "react";
import { useSpring, animated } from "react-spring";
import { theme } from "../../config";
export const PlayButton = (props) => {
  const [horver, setHorver] = useState(false);
  const animation = useSpring({
    backgroundColor: horver ? theme.primary : theme.backgroundColor,
    color: horver ? theme.backgroundColor : theme.primary,
    from: { color: theme.backgroundColor },
  });
  return (
    <animated.div
      style={{
        ...animation,
        display: "inline-block",
        position: "absolute",
        borderRadius: "50%",
      }}
    >
      <IoIosPlay
        {...props}
        style={{
          color: "default",
          fontSize: 80,
        }}
        onMouseMove={() => setHorver(true)}
        onMouseLeave={() => setHorver(false)}
      />
    </animated.div>
  );
};
