import { useState, useEffect, Fragment } from "react";
import { gql, useQuery } from "@apollo/client";
import { getErrorMessage } from "../../lib/chip";
import { Item } from "./item";
import { Loading } from "../src/Loading";
import { MdCreate, MdChangeHistory, MdTrackChanges } from "react-icons/md";
import {
  IoMdAddCircleOutline,
  IoIosColorWand,
  IoIosSwap,
  IoIosAdd,
  IoIosAddCircle,
  IoIosAddCircleOutline,
} from "react-icons/io";
import { Create } from "./create";
import { customerVar, refetchCustomer } from "../../apollo/client";
import { Link } from "../src/Link";
import theme from "../src/theme";
import { css } from "../src/css";
import { chooseCustomer } from "../../apollo/action";
import { useRouter } from "next/router";
// get
const GET_CUSTOMERS = gql`
  query {
    customer @client
    allCustomers {
      id
      name
      phone
      address
    }
  }
`;

export function List() {
  const { data, loading, error, refetch } = useQuery(GET_CUSTOMERS);
  refetchCustomer(refetch);
  if (loading) return <Loading />;
  if (error) return null;
  const customer = data?.customer;
  const allCustomers = data?.allCustomers;

  // default first customer
  if (!customer?.id && allCustomers?.length) chooseCustomer(allCustomers[0]);

  const [create, setCreate] = useState(false);
  const router = useRouter();
  return allCustomers?.length ? (
    <div
      style={{
        marginBottom: theme.spacing(3),
        paddingBottom: theme.spacing(3),
      }}
    >
      {create ? <Create /> : null}
      <h5 style={{ ...css.h5, display: "inline", marginRight: 13 }}>
        Chọn địa chỉ bên dưới
      </h5>
      <label
        onClick={() => {
          setCreate(true);
        }}
        style={{ cursor: "pointer", color: theme.color }}
      >
        Hoăc tạo mới <IoIosAddCircleOutline />
      </label>
      {allCustomers?.length
        ? allCustomers.map((customer) => (
            <Item key={customer.id} customer={customer} />
          ))
        : null}
    </div>
  ) : (
    <Create />
  );
}
