import { page } from "../../config/index";

import { IoIosHeartEmpty, IoIosHeart } from "react-icons/io";
import { MdCompareArrows, MdDoneAll } from "react-icons/md";
import {
  removeWishListItem,
  removeCompareItem,
  addProductToLocalWishlist,
  addProductToLocalCompare,
} from "../../apollo/action";
import { useQuery, gql } from "@apollo/client";
import { useState } from "react";

const style = {
  button: (top = -5, color = theme.backgroundColor) => ({
    position: "absolute",
    top,
    right: -5,
    padding: 3,
    paddingTop: 5,
    width: 30,
    height: 30,
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    borderRadius: "50%",
    color,
    fontWeight: 800,
    backgroundColor: theme.primary,
  }),
};
import { theme } from "../../config/index";

export function ImgProduct({ product, style }) {
  const { data } = useQuery(gql`
    query {
      wishlist @client
    }
  `);

  const isInWishlist = data?.wishlist?.some((item) => item.id === product.id);

  let ImgSrcs = product?.images || [];
  if (product.image) {
    ImgSrcs = [{ file: product.image }].concat(ImgSrcs);
  }
  const [imgIndex, setImgIndex] = useState(0);

  return (
    <div
      style={{
        ...style,
        position: "inherit",
      }}
    >
      <img
        src={
          ImgSrcs[imgIndex]
            ? page.server + ImgSrcs[imgIndex]?.file?.publicUrl
            : "/assets/img/no-image.jpg"
        }
        style={{ width: "100%", borderRadius: theme.spacing(2) }}
        key={ImgSrcs[imgIndex]?.file?.publicUrl}
      />

      {/* icon wishlist */}
      <i
        onClick={() =>
          isInWishlist
            ? removeWishListItem(product)
            : addProductToLocalWishlist(product)
        }
        style={style.button()}
      >
        {isInWishlist ? <MdDoneAll /> : <IoIosHeartEmpty />}
      </i>
    </div>
  );
}
