import {
  DropdownItem,
  DropdownMenu,
  DropdownToggle,
  UncontrolledDropdown,
} from "reactstrap";
import { gql, useQuery } from "@apollo/client";

import { Link } from "../src/Link";
import { USER } from "../../apollo/action";
import { Fragment, useState } from "react";
import { AiOutlineUser } from "react-icons/ai";
import { theme } from "../../config/index";
import { css } from "../src/css";
import { SignIn } from "./signin";
import { SignUp } from "./signup";

export function CategoriesDropdownMenu({ onClick }) {
  const [onSignIn, setOnSignIn] = useState(false);
  const [onSignUp, setOnSignUp] = useState(false);
  const { data } = useQuery(USER);
  return typeof window !== "undefined" ? (
    data?.user?.id ? (
      <UncontrolledDropdown nav inNavbar style={{ listStyle: "none" }}>
        <DropdownToggle
          nav
          caret
          style={{ color: theme.primary, margin: 0, padding: 0 }}
        >
          <AiOutlineUser style={css.iconBorder} />
        </DropdownToggle>
        <DropdownMenu
          right
          style={{ backgroundColor: theme.backgroundColor, color: theme.color }}
        >
          <DropdownItem>
            <a onClick={onClick} style={{ color: theme.color }}>
              Hi {data?.user?.isSeller ? "seller" : null} {data.user.email}
            </a>
          </DropdownItem>
          <Link
            href="/signout"
            onClick={onClick}
            style={{ color: theme.color }}
          >
            <DropdownItem href="" style={{ color: theme.color }}>
              Đăng Xuất
            </DropdownItem>
          </Link>
        </DropdownMenu>
      </UncontrolledDropdown>
    ) : (
      // <Link
      //   href="/signin"
      //   style={{ color: theme.primary, margin: 0, padding: 0 }}
      // >
      //   <AiOutlineUser style={css.iconBorder} />
      // </Link>
      <Fragment>
        <div
          style={{ color: theme.primary, margin: 0, padding: 0 }}
          onClick={() => {
            setOnSignIn(true);
          }}
        >
          <AiOutlineUser style={css.iconBorder} />
        </div>
        <SignIn onSignIn={onSignIn} setOnSignIn={setOnSignIn} />
        <SignUp onSignUp={onSignUp} setOnSignUp={setOnSignUp} />
      </Fragment>
    )
  ) : (
    <label>server</label>
  );
}
