import { gql, useQuery, useMutation } from "@apollo/client";

import { emptyCart } from "../../apollo/action";
import {
  customerVar,
  newOrderVar,
  newOrderCountVar,
} from "../../apollo/client";
import { Item as CartItem } from "./item";
import { useRouter } from "next/router";
import { page } from "../../config/index";

import { formatMoney, getErrorMessage } from "../../lib/chip";
import { Fragment } from "react";
import { css } from "../src/css";
import theme from "../src/theme";
import { Loading } from "../src/Loading";
import { Link } from "../src/Link";
import { ChooseCustomer } from "../Customer/ChooseCustomer";
export const CART = gql`
  query {
    cartItems @client
    customer @client
    user @client
  }
`;

export function List() {
  // loading data
  const { data: cartData, loading, error } = useQuery(CART);
  if (loading) return <Loading />;
  if (error) return null;
  const customer = cartData?.customer;
  let sum = 0;
  cartData?.cartItems?.map((cartItem) => {
    sum += cartItem.price;
  });

  return (
    <div>
      <h5 style={css.h5}>Tổng</h5>
      <p>{formatMoney(sum)}</p>
      {cartData ? (
        cartData?.cartItems?.length === 0 ? (
          <p>Bạn chưa chọn sản phẩm nào!</p>
        ) : (
          cartData?.cartItems?.map((cartItem) => {
            return <CartItem key={cartItem.product.id} cartItem={cartItem} />;
          })
        )
      ) : null}
    </div>
  );
}
