import { List as Posts } from "../components/Post/list";
import { Container } from "reactstrap";
import { Post } from "../components/Post/detail";
import { useRouter } from "next/router";
import { useQuery, gql } from "@apollo/client";
import { Loading } from "../components/src/Loading";
import { css } from "../components/src/css";
import { useEffect } from "react";
const GET_PRODUCTS = gql`
  query($url: String) {
    allPosts(first: 1, where: { url: $url }) {
      id
      url
      file {
        publicUrl
      }
      title
      hashtags {
        name
        url
      }
      description
    }
  }
`;
export default function PostDetali() {
  const router = useRouter();
  const { detail } = router.query;
  let { data, error, loading } = useQuery(GET_PRODUCTS, {
    variables: { url: detail },
  });
  useEffect(() => {
    if (data)
      window.scroll({
        top: 0,
        left: 0,
        behavior: "smooth",
      });
  });
  if (error || loading) return <Loading />;
  const post = data?.allPosts?.length ? data.allPosts[0] : null;

  return post ? (
    <Container>
      <Post post={post} />
      <h5 style={css.h5}>Sản Phẩm Liên Quan</h5>
      <Posts hashtag={post.hashtag.url} except={detail} more={false} />
    </Container>
  ) : null;
}
