import { Fragment } from "react";
import { gql, useQuery } from "@apollo/client";
import { Item as Category } from "../Category/item";
import { Item as Attributes } from "../Attribute/item";
const GET_CATE = gql`
  query($category: String, $attributes: [AttributeWhereInput]) {
    allCategories(where: { url: $category }) {
      id
      name
      url
    }
    allAttributes(where: $attributes) {
      id
      name
      url
    }
  }
`;
export const Filter = ({
  category,
  brand,
  search,
  price_from,
  price_to,
  attributes,
}) => {
  const { data } = useQuery(GET_CATE, {
    variables: {
      category,
      attributes: attributes
        ? attributes?.map((attr) => ({ id: attr.id }))
        : [],
    },
  });

  return (
    <Fragment>
      <Category categories={data?.allCategories || []} />
    </Fragment>
  );
};
