import { useRouter } from "next/router";
import { List as Products } from "../components/Product/list";

import { toAttributeGourpWhereInput } from "../lib/chip";
import { useState, useEffect, Fragment } from "react";
import { Container, Row, Col } from "reactstrap";
import { Sidebar } from "../components/Sidebar";
import { theme } from "../config/index";

import { Filter } from "../components/Product/filter";
import { List as Banners } from "../components/Banner/list";
import { css } from "../components/src/css";
import { IntroBox } from "../components/IntroBox";
import { IoIosPlayCircle } from "react-icons/io";
import { PlayButton } from "../components/src/PlayButton";
const Index = () => {
  const router = useRouter();
  let query = router.query;
  const category = query.category;
  const categories = query.categories;
  const brand = query.brand;
  const search = query.search;
  const price_from = query.price_from ? Number(query.price_from) : 0;
  const price_to = query.price_to ? Number(query.price_to) : 999999999;
  const attributes = toAttributeGourpWhereInput(
    query.attributes ? query.attributes.split(",") : []
  );

  return (
    <Fragment>
      <section style={{ marginTop: theme.spacing(4) }}>
        <Container>
          <Row>
            <Col xs={12} md={6}>
              <div>
                <h1 style={{ ...css.h2, textAlign: "center" }}>
                  Câu Chuyện Sản Phẩm
                </h1>
                <p>
                  Việt Nam là một trong 8 quốc gia trên thế giới có tổ Yến.
                  Chính nhờ đặc tính giàu dinh dưỡng, cũng như sự gian nan, cầu
                  kỳ trong quá trình thu thập, chế biến mà Yến Sào đã trở thành
                  món ăn quý giá từ muôn đời nay.
                </p>
                <p>
                  Yến Sào được xếp vào bát trân (8 món ăn thuộc hàng tuyệt phẩm,
                  chỉ vua chúa mới được sử dụng), bên cạnh nem công, chả phượng,
                  da tê ngưu, bàn tay gấu, gân nai, môi đười ươi và thịt chân
                  voi. Tất cả được xem là tinh túy của thế giới muốn loài, được
                  vua Minh Mạng ví như "mảnh trăng non thuần khiết".
                </p>
                <p>
                  Với điều kiện khí hậy khắc nghiệt của miền Trung đã chọn lọc
                  tự nhiên và tồn tại một loài chim Yến có sức chống chọi mãnh
                  liệt với khí hậu thời tiết khắc nghiệt. Nguồn gen này được lưu
                  giữ tồn tại ở hang đảo Cù Lao Chàm. Tổ yến ở đây được giới ẩm
                  thực Hồng Koong đánh giá rất cao về giá trị dinh dưỡng và giá
                  trị ẩm thực. Trên thực tế, giá Yến Cù Lao Chàm cao gấp nhiều
                  lần so với địa phương khác.
                </p>
                <p>
                  Với nghệ thuật dẫn dụ được đàn chim non từ hang đảo Cù Lao
                  Chàm về ở lại trong các nhà nuôi (hang động nhân tạo) ven
                  biển, kết hợp với công nghệ mới của Công ty Yến Sào Đất Quảng,
                  chúng tôi đã dùng những thanh đá tự nhiên ở Quảng Nam ghép
                  thành những vách đá tự nhiên cho chim xây tổ. Công nghệ mới
                  này đã cho ra dòng sản phẩm hoàn toàn tự nhiên có hình thức và
                  chất lượng tương đồng với Yến đảo Cù Lao Chàm.
                </p>
                <p>
                  Sản phẩm Yến đá của hệ thống nhà yến Đất Quảng tự hào đã giữ
                  nguyên được gái trị hoang dã của tổ yến tự nhiên, nâng niu giữ
                  gìn được sản vật tinh túy của biển trời Quảng Nam.
                </p>
                <p>
                  Thưởng thức Yến Sào Đất Quảng là nếm vị ngon của tinh hoa biển
                  trời Đất Quảng, là vị ngọt ngào của miền đất mẹ Quảng Nam.
                </p>
              </div>
            </Col>
            <Col xs={12} md={6}>
              <div
                style={{
                  position: "relative",
                  display: "flex",
                  alignItems: "center",
                  justifyContent: "center",
                }}
              >
                <Banners style={{ height: "60vh" }} />
                <PlayButton />
              </div>
            </Col>
          </Row>
        </Container>
      </section>
      <Container fluid style={{ marginTop: theme.spacing(3) }}>
        <IntroBox />
      </Container>
      <Container fluid>
        <Row noGutters>
          <Col xs={4} md={3} lg={3} xl={2}>
            <Sidebar
              onSearch={(search) => {
                setSearch(search);
              }}
            />
          </Col>
          <Col xs={8} md={9} lg={9} xl={10}>
            <div>
              {category ||
              categories ||
              brand ||
              search ||
              attributes ||
              price_from ||
              price_to != 999999999 ? (
                <Fragment>
                  <Filter
                    category={category}
                    categories={categories}
                    brand={brand}
                    search={search}
                    price_from={price_from}
                    price_to={price_to}
                    attributes={
                      query.attributes ? query.attributes.split(",") : []
                    }
                  />
                  <Products
                    sm={6}
                    lg={4}
                    xl={3}
                    category={category}
                    categories={categories}
                    brand={brand}
                    search={search}
                    price_from={price_from}
                    price_to={price_to}
                    attributes={attributes}
                  />
                </Fragment>
              ) : (
                <Fragment>
                  <div style={css.filter}>
                    <h6
                      style={{
                        ...css.h6,
                        display: "inline-block",
                        marginRight: theme.spacing(2),
                      }}
                    >
                      Sản Phẩm:{" "}
                    </h6>
