import { useState, Fragment } from "react";
import { useRouter } from "next/router";
import { gql } from "@apollo/client";
import { useMutation } from "@apollo/client";
import { onSignIn, init } from "../../apollo/action";
import { getErrorMessage } from "../../lib/chip";
import { useApollo } from "../../apollo/client";
import { css } from "../src/css";
import { theme } from "../../config/index";
import { IoIosPerson } from "react-icons/io";

export const SignInMutation = gql`
  mutation($email: String!, $password: String!) {
    authenticateUserWithPassword(email: $email, password: $password) {
      token
      item {
        id
        email
        isAdmin
        isSeller
      }
    }
  }
`;

export function SignIn({ onSignIn, setOnSignIn }) {
  const router = useRouter();
  const client = useApollo();
  const [signIn] = useMutation(SignInMutation);

  const [errorMsg, setErrorMsg] = useState();
  const redirect = router.query?.redirect;
  async function handleSubmit(event) {
    event.preventDefault();
    const email = event.currentTarget.elements.email.value;
    const password = event.currentTarget.elements.password.value;
    try {
      await client.clearStore();
      const { data } = await signIn({
        variables: {
          email: email,
          password: password,
        },
      });
      if (data.authenticateUserWithPassword) {
        const {
          authenticateUserWithPassword: { token, item },
        } = data;
        localStorage.setItem("token", token);
        await client.resetStore();
        await router.push({ pathname: redirect ? redirect : "/" });

        init();
        onSignIn({ user: item });
      }
    } catch (error) {
      setErrorMsg(getErrorMessage(error));
    }
  }
  return onSignIn ? (
    <div
      style={{
        position: "fixed",
        display: "flex",
        alignItems: "center",
        top: 0,
        left: 0,
        width: "100vw",
        height: "100vh",
        zIndex: 1000,
        backgroundColor: "rgba(0,0,0,0.8)",
        padding: theme.spacing(3),
      }}
    >
      <div
        onClick={() => {
          setOnSignIn(false);
        }}
        style={{
          width: "100%",
          height: "100%",
          zIndex: -1,
          position: "absolute",
        }}
      ></div>
      <section
        style={{
          ...css.box,
          marginTop: 34,
          marginBottom: 34,
          marginLeft: "auto",
          marginRight: "auto",
          padding: theme.spacing(5),
          backgroundColor: theme.backgroundColor,
          width: 500,
          maxWidth: "100%",
        }}
      >
        <h2 style={{ ...css.h2, textAlign: "center" }}>Đăng Nhập</h2>
        <form noValidate onSubmit={handleSubmit}>
          <h5 style={css.h5}>
            <IoIosPerson style={{ ...css.iconHeader }} />
            Tài khoản
          </h5>
          <input
            required
            id="email"
            label="Tài khoản"
            name="email"
            placeholder="Nhập Tài khoản"
            style={{
              ...css.input,
              width: "100%",
              marginBottom: theme.spacing(5),
              borderRadius: theme.spacing(2),
            }}
          />
          <h5 style={css.h5}>Mật khẩu</h5>
          <input
            required
            name="password"
            label="Mật khẩu"
            placeholder="Nhập Mật khẩu"
            type="password"
            id="password"
            style={{
              ...css.input,
              width: "100%",
              marginBottom: theme.spacing(4),
              borderRadius: theme.spacing(2),
            }}
          />

          <button
            type="submit"
            style={{
              ...css.button,
              marginTop: 21,
            }}
          >
            Tiếp Tục
          </button>
          <p> {errorMsg}</p>
        </form>
      </section>
    </div>
  ) : null;
}
