import { CategoriesDropdownMenu as Users } from "../User/DropdownUser";
import { Icon as CartIcon } from "../Cart/icon";
import { Icon as WishlistIcon } from "../Wishlist/icon";
import { Icon as CompareIcon } from "../Compare/icon";
import { Icon as OrderIcon } from "../Order/icon";
import { CategoriesDropdownMenu } from "../Category/DropdownMenu";
import Link from "next/link";
import {
  Collapse,
  Navbar,
  NavbarToggler,
  NavbarBrand,
  Nav,
  NavItem,
  NavbarText,
  Container,
  NavLink,
  Form,
  ButtonToggle,
} from "reactstrap";
import { useState, useEffect, Fragment } from "react";
import { Search } from "../Search";
import { page } from "../../config/yensaodatquang.json";
import { Logo } from "../src/logo";
import { IoIosMenu, IoIosArrowBack } from "react-icons/io";
import { FiFilter } from "react-icons/fi";
import { useRouter } from "next/router";
import { theme } from "../../config/yensaodatquang.json";
export default function MenuApp({ isOpen, toggle }) {
  const [isFixed, setIsFixed] = useState(false);
  const [innerWidth, setInnerWidth] = useState();
  const router = useRouter();
  useEffect(() => {
    setInnerWidth(window.innerWidth);
  });
  return (
    <Navbar
      expand="md"
      style={{
        position: "fixed",
        backgroundColor: theme.backgroundColor,
        width: "100%",
        zIndex: 100,
        boxShadow: `3px 3px 15px ${theme.backgroundColor}`,
      }}
    >
      <Container>
        <NavbarBrand style={{ display: "flex", alignItems: "center" }}>
          {/* BACK */}
          <IoIosArrowBack
            style={{ margin: 15 }}
            onClick={() => {
              router.back();
            }}
          />

          {/* LOGO */}
          <Logo
            onClick={() => {
              router.push("/");
            }}
          />
        </NavbarBrand>
        {/* CART ICON */}

        <CartIcon
          style={{
            display: innerWidth < 768 ? "inline-block" : "none",
          }}
        />
        {/* ICON MENU */}
        <IoIosMenu
          onClick={toggle}
          style={{
            display: innerWidth < 768 ? "inline-block" : "none",
            width: "auto",
            float: "right",
            padding: 0,
            margin: 8,
          }}
        />
        <Collapse isOpen={isOpen} navbar>
          <Nav className="mr-auto" navbar>
            <NavItem>
              <Link href="/products">
                <NavLink href="">Xem Sản Phẩm</NavLink>
              </Link>
            </NavItem>

            {/* <CategoriesDropdownMenu /> */}

            <Search />
          </Nav>
          <Nav
            className="hide-sm"
            style={{
              display: "flex",
              alignItems: "center",
              justifyContent: "center",
            }}
          >
            {/* Compare */}

            <CompareIcon page />

            {/* Wishlist */}

            <WishlistIcon page />

            {/* Cart */}

            <CartIcon page />

            {/* Order */}

            <OrderIcon page />

            <Users />
          </Nav>
        </Collapse>
      </Container>
    </Navbar>
  );
}
