import { OrderItems } from "./orderItems";
import { Row, Col } from "reactstrap";
import { formatMoney } from "../../lib/chip";
import { Divider } from "../src/Divider";
import { theme, page } from "../../config/index";

import { css } from "../src/css";
export function Item({ order, user }) {
  const stepName = [
    "Đặt thành công",
    "Đã đóng gói",
    "Đã vận chuyển",
    "Đã thanh toán",
    "Hoàn thành",
  ];

  const isSeller = user.isSeller == true && user.id == page.seller.id;
  console.log(order);
  return (
    <div
      style={{
        borderRadius: 8,
        padding: 15,
        margin: 15,
        backgroundColor: theme.backgroundColor,
      }}
    >
      <Row>
        <Col sm={12} md={6}>
          <div style={{ marginBottom: theme.spacing(3) }}>
            <h5 style={css.h5}>Địa Chỉ:</h5>
            <a style={{ display: "block" }}>{order?.customer?.name}</a>
            <a style={{ display: "block" }}>{order?.customer?.phone}</a>
            <a style={{ display: "block" }}>{order?.customer?.address}</a>
          </div>
          <div style={{ marginBottom: theme.spacing(3) }}>
            <h5 style={css.h5}>Trạng Thái: </h5>
            {stepName.map((step, index) =>
              index <= order.step ? (
                <i key={index}>
                  {index != 0 ? " > " : null} {step}
                </i>
              ) : null
            )}
          </div>
          <a style={{ marginBottom: theme.spacing(4), display: "block" }}>
            Tổng: {formatMoney(order.total)}
          </a>

          {isSeller ? (
            <button style={{ ...css.button }}>
              Xác Nhận {stepName[Number(order.step) + 1]}
            </button>
          ) : null}
        </Col>

        <Col>
          <div style={{ marginBottom: theme.spacing(3) }}>
            <OrderItems items={order.items} />
          </div>
        </Col>
        <Col xs={12}></Col>
      </Row>
    </div>
  );
}
