import { Row, Container, Col } from "reactstrap";
import { theme } from "../../config";

const Item = ({ src }) => {
  return (
    <div style={{ position: "relative" }}>
      <img
        src={src}
        style={{ width: "100%", borderRadius: theme.spacing(2), zIndex: -1 }}
      />
      <h5
        style={{
          position: "absolute",
          zIndex: 99,
          bottom: theme.spacing(3),
          color: theme.backgroundColor,
        }}
      >
        OK
      </h5>
    </div>
  );
};
export const Feature = () => {
  return (
    <section
      style={{
        marginTop: theme.spacing(7),
        marginBottom: theme.spacing(7),
        paddingTop: theme.spacing(6),
        paddingBottom: theme.spacing(6),
        backgroundColor: theme.backgroundColor,
      }}
    >
      <Container>
        <Row>
          <Col xs={12} md={4}>
            <Item src={"/yensaodatquang/assets/img/xay-dung-nha-yen.JPG"} />
          </Col>
          <Col xs={12} md={4}>
            <Item src={"/yensaodatquang/assets/img/khai-thac-to-yen.JPG"} />
          </Col>
          <Col xs={12} md={4}>
            <Item src={"/yensaodatquang/assets/img/gia-cong-to-yen.JPG"} />
          </Col>
        </Row>
      </Container>
    </section>
  );
};
