import { useRouter } from "next/router";
import { toAttributeGourpWhereInput, formatMoney } from "../../lib/chip";
import { useState, useRef } from "react";
import { List as Attributes } from "../Attribute/list";
import { List as Categories } from "../Category/list";
import { List as Brands } from "../Brand/list";
import { Search } from "../Search/index";
import { Navbar, NavbarBrand, NavbarToggler, Collapse } from "reactstrap";
import { FiFilter } from "react-icons/fi";
import { css } from "../src/css";
import { theme, page } from "../../config/index";

import {
  IoIosSearch,
  IoIosPricetag,
  IoIosPricetags,
  IoIosColorFilter,
} from "react-icons/io";

export function Sidebar() {
  const router = useRouter();
  let query = router.query;
  const price_from = query.price_from ? Number(query.price_from) : 0;
  const price_to = query.price_to ? Number(query.price_to) : 0;
  //
  const [priceFrom, setPriceFrom] = useState(0);
  const [priceTo, setPriceTo] = useState(99999999);
  //
  const onSubmit = (e) => {
    /**
     * Prevent submit from reloading the page
     */
    e.preventDefault();
    e.stopPropagation();
    query.price_from = priceFrom;
    query.price_to = priceTo;
    router.push({ query });
  };
  const [collapsed, setCollapsed] = useState(true);
  const scrollToRef = (ref) => window.scrollTo(0, ref.current.offsetTop);
  const top = useRef(null);
  const tg = () => setCollapsed(!collapsed);
  return (
    <section style={{ paddingTop: theme.spacing(4) }}>
      <div style={css.box}>
        <h5 style={css.h5}>
          <IoIosSearch style={css.iconHeader} />
          Tìm Kiếm
        </h5>
        <Search style={{ width: "100%", marginBottom: theme.spacing(3) }} />
      </div>
      <div style={css.box}>
        <Categories />
      </div>
      {/* KHOANG GIA */}
      <div style={css.box}>
        <h5 style={css.h5}>
          <IoIosPricetag style={css.iconHeader} />
          Giá
        </h5>
      </div>
      <form onSubmit={onSubmit} action="" style={css.box}>
        <h5 style={css.h5}>
          <IoIosPricetag style={css.iconHeader} />
          Giá
        </h5>
        {page.prices.map((price, index) => {
          console.log(price, index);
          return (
            <div
              style={{
                fontSize: "0.8rem",
                borderRadius: theme.spacing(1),
                padding: theme.spacing(1),
                marginBottom: theme.spacing(2),
                textAlign: "center",
                border: "1px solid rgba(0,0,0,0.08)",
                cursor: "pointer",
              }}
              onClick={() => {
                query.price_from = index > 0 ? page.prices[index - 1] : 0;
                query.price_to = price;
                router.push({ query });
              }}
            >
              {index > 0 ? formatMoney(page.prices[index - 1]) : 0}
              {" - "}
              {formatMoney(price)}
            </div>
          );
        })}
        <input
          placeholder="Từ"
          name="price_from"
          onChange={(event) => {
            setPriceFrom(event.target.value);
          }}
          defaultValue={price_from ? price_from : ""}
          style={{ ...css.input, width: "45%" }}
        />
        <div
          style={{ width: "10%", display: "inline-block", textAlign: "center" }}
        >
          -
        </div>

        <input
          placeholder="Đến"
          name="price_to"
          onChange={(event) => {
            setPriceTo(event.target.value);
          }}
          defaultValue={price_to ? price_to : ""}
          style={{ ...css.input, width: "45%" }}
        />
        <button
          type="submit"
          style={{ ...css.button, marginTop: theme.spacing(3) }}
        >
          Lọc
        </button>
      </form>
      <div style={css.box}>
        <Attributes />
      </div>
      <div style={css.box}>
        <Brands />
      </div>
    </section>
  );
}
