import { formatMoney } from "../../lib/chip";
import { Link } from "../src/Link";

import { useState, Fragment, useContext } from "react";

import { ImgPost } from "./imagePost";
import { FadeIn } from "../Animations/FadeIn";

import { useRouter } from "next/router";
import { SellerContext } from "../src/SellerProvider";

export const Item = ({ post }) => {
  const theme = useContext(SellerContext);
  const [show, setShow] = useState(false);
  const router = useRouter();
  console.log(1);
  return (
    <FadeIn>
      <div
        style={{
          ...theme.css.box,
          padding: 0,
          position: "relative",
        }}
      >
        <ImgPost
          post={post}
          style={{
            marginBottom: 0,
            borderRadius: `${theme.spacing(1)}px ${theme.spacing(1)}px 0px 0px`,
          }}
          onClick={() => {
            router.push({
              pathname: "/detail",
              query: { detail: post.url },
            });
          }}
        />
        <div style={{ padding: theme.spacing(2) }}>
          <Link
            href={{
              pathname: "/detail",
              query: { detail: post.url },
            }}
          >
            <a>
              <p
                style={{
                  color: theme.color,
                  textTransform: "capitalize",
                }}
              >
                {post.name}
              </p>
            </a>
          </Link>
          <p
            style={{
              textDecoration: post.sale ? "line-through" : "",
              color: post.sale ? theme.secondary : theme.primary,
              fontSize: post.sale ? "0.8rem" : "0.85rem",
              float: post.sale ? "right" : "default",
              fontWeight: post.sale ? "normal" : "bold",
              marginBottom: theme.spacing(0),
              wordWrap: "break-word",
            }}
          >
            {formatMoney(post.price)}
          </p>

          {post.sale ? (
            <p
              style={{
                color: theme.primary,
                display: show ? "none" : "block",
                fontWeigh: "bold",
                fontSize: "0.85rem",
                marginBottom: theme.spacing(0),
              }}
            >
              {formatMoney(post.price - post.sale)}
            </p>
          ) : null}

          {/* <button
          onClick={() => {
            addPostToLocalCart({ post });
          }}
          style={{ marginBottom: 0 }}
        >
          Thêm vào giỏ
        </button> */}
        </div>
      </div>
    </FadeIn>
  );
};
