import { ApolloProvider } from "@apollo/client";

import "bootstrap/dist/css/bootstrap.min.css";
import "../public/yensaodatquang/assets/css/style.css";
import MenuApp from "../components/MenuApp/index";
import { useApollo } from "../apollo/client";
import { Footer } from "../components/src/Footer";
import { useState, useEffect } from "react";
import { useRouter } from "next/router";
import { theme, page } from "../config/index";
import { FacebookProvider, CustomChat } from "react-facebook";
export default function App({ Component, pageProps }) {
  const apolloClient = useApollo(pageProps.initialApolloState);

  const [isOpen, setIsOpen] = useState(false);
  const [innerWidth, setInnerWidth] = useState();

  const toggle = () => setIsOpen(!isOpen);
  const router = useRouter();
  useEffect(() => {
    router.events.on("routeChangeComplete", (url) => {
      setIsOpen(false);
    });
    setInnerWidth(window.innerWidth);
  });
  return (
    <ApolloProvider client={apolloClient}>
      {/* <MenuApp isOpen={isOpen} toggle={toggle} innerWidth={innerWidth} /> */}
      <header>
        <img
          data-aos="fade-down"
          src="/loaloa/assets/img/wave(1).svg?h=7137c98ce92618b144daf992371d8838"
          style={{ position: "fixed", zIndex: -1, width: "100%" }}
        />
        <nav
          className="navbar navbar-light navbar-expand-md navigation-clean py-2"
          style={{ backgroundColor: "transparent", zIndex: 10 }}
        >
          <div className="container">
            <a className="navbar-brand p-0" href="#">
              <h6 className="text-uppercase m-0 p-0">
                <label className="text-primary">
                  <i className="far fa-gem mr-2"></i>
                </label>
                loa loa team
              </h6>
            </a>
            <button
              data-toggle="collapse"
              className="navbar-toggler"
              data-target="#navcol-1"
            >
              <span className="sr-only">Toggle navigation</span>
              <span className="navbar-toggler-icon"></span>
            </button>
            <div className="collapse navbar-collapse" id="navcol-1">
              <ul className="nav navbar-nav ml-auto">
                <li className="nav-item" role="presentation">
                  <a className="nav-link" href="#demo">
                    Xem mẫu
                  </a>
                </li>
                <li className="nav-item" role="presentation">
                  <a className="nav-link" href="#price">
                    Bán hàng Online
                  </a>
                </li>
                <li className="nav-item" role="presentation">
                  <a className="nav-link" id="advance" href="#brand">
                    Khẳng định thương hiệu
                  </a>
                </li>
              </ul>
            </div>
          </div>
        </nav>
      </header>
      <main>
        <Component {...pageProps} />
      </main>
      <footer>
        <Footer />
      </footer>
    </ApolloProvider>
  );
}
